<div class='sangar-content'>	<?php 		$sliderurl = get_post_meta( get_the_ID(),'rsris_bdt_link', true );		$content = get_the_title();		$content = strip_tags($content);		$summary = $this->bdt_truncate($content, 50);				if($sliderurl != '')		{	?>		<a href="<?php echo $sliderurl; ?>" title="<?php echo $summary; ?>"><?php echo $summary;?></a>			<?php the_post_thumbnail( 'full' ); ?>		</a>		<?php }else{			the_post_thumbnail( 'full' );		} ?></div>